import React from 'react';
import ServiceCard from './ServiceCard';
import ServiceInfo from './ServiceInfo';
import { notificationService } from '../services/NotificationService';
import { progressService } from '../services/ProgressService';
import '../styling/ChooseService.css'

class ChooseService extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            services: [],
            progress: 0
        };
    }

    async componentDidMount() {
        const interval = setInterval(() => {
            let currentProgress = this.state.progress;
            if (currentProgress < 90) {
                currentProgress = currentProgress + 1;
                this.setState({ progress: currentProgress });
                progressService.setCurrentProgress(this.state.progress);
            }
        }, 50);
        try {
            let response = await fetch('http://localhost:8080/api/services/retrieveAvailableSalonServices');
            let services = await response.json();
            clearInterval(interval);
            this.setState({ progress: 100 });
            progressService.setCurrentProgress(this.state.progress);
            this.setState({ services: services });
        } catch(err) {
            clearInterval(interval);
            this.setState({ progress: 100 });
            progressService.setCurrentProgress(this.state.progress);
            notificationService.notifyError("Something went wrong! : " + err);
        }
        setTimeout(() => {
            progressService.clearProgress();
            this.setState({ progress: 0 });
        }, 1000);
    }

    render() {
        const { services } = this.state;
        return (
            <div className="ChooseService">
            <div className="cards">
            {
                salonServices.map(salonService =>

                    <ServiceCard
                        name={salonService.name}
                        price={salonService.price}
                        description={salonService.description}
                        timeInMinutes={salonService.timeInMinutes}
                    />


                )

            }
    </div>

    </div>
    );
    }
}

export default ChooseService;
